	always @(fchiram or dec_cpurd_enable or dec_cpuwr_enable or pa_st2) begin
		if (fchiram) begin
			slexm_en = (dec_cpuwr_enable & ~pa_st2) | (dec_cpurd_enable | ~dec_cpuwr_enable | pa_st2) ;
		end
