	//synopsys async_set_reset "resb"
	always @(posedge baseck or negedge resb) begin
		if (!resb) begin
			exma <= 4'h0 ;
			ma_pre <= 16'h0000 ;
			slmem_pre <= 1'b0 ;
			slreg <= 1'b0 ;
