			casex ({ifbr_not,ifbr_zero,ifbr_ht})
				3'b000	: stage_cut = ~CY ;
				3'b100	: stage_cut = CY ;
				3'b010	: stage_cut = ~Z ;
				3'b110	: stage_cut = Z ;
				3'b001	: stage_cut = (Z | CY) ;
				3'b101	: stage_cut = ~(Z | CY) ;
				default	: stage_cut = 1'b0 ;
