			if (pc_wait_flg)	prefix_skp <= prefix_skp ;
			else if (skpack && !(fchiram_skp || romrd_skp) && (ID_stage0 == 8'h11))
						prefix_skp <= 1'b1 ;
			else			prefix_skp <= 1'b0 ;
